package com.weatherReports.webservicesimport com.weatherReports.model.ResponseWeatherCurrentimport com.weatherReports.model.ResponseWeatherForecastimport com.weatherReports.utils.MyConstantsimport io.reactivex.Observableimport retrofit2.http.*interface ApiInterface {    // q=Noida    //  APPID=e39a45e7c5961f0f6573c0774f4f1732    // units=metric    @GET("http://api.openweathermap.org/data/2.5/weather")    fun weatherCurrentApi(        @Query("q") q: String,        @Query("APPID") APPID: String="e39a45e7c5961f0f6573c0774f4f1732",        @Query("units") units: String="metric"    ): Observable<ResponseWeatherCurrent>    //  http://api.openweathermap.org/data/2.5/forecast?q=Delhi&APPID=e39a45e7c5961f0f6573c0774f4f1732&Metric=Celsius    @GET("http://api.openweathermap.org/data/2.5/weather")    fun weatherForecastApi(        @Query("q") q: String,        @Query("APPID") APPID: String="e39a45e7c5961f0f6573c0774f4f1732",        @Query("Metric") units: String="Celsius"    ): Observable<ResponseWeatherForecast>}